import React, { useState } from 'react';

export default function Events() {

    const purple="red";

    const [bg , setBg] = useState(purple);
    
    

    const bgChange = () =>{
        // console.log("Vipin chauhan");
        let newBg = 'Yellow';
        setBg(newBg);
    }

    return (
        <>  
         <div style={{ backgroundColor: bg }}>
            <button style={{padding:"12px 20px", backgroundColor:'black',color:'white',border:"2px solid #ecf0f1", outline:"none", borderRadius:"5px", textTransform:"uppercase",cursor:"pointer"}} onClick={bgChange}>Click Me</button>
        </div>
        </>
    )
}
